mapper.select:
SELECT A.UUID, A.DOCID, A.DOCNAME, A.NICKNAME, A.PHONE, A.GENDER, A.SELFAGE, A.EMAIL, A.PORTRAIT, A.TIP, B.DEP_NAME AS DEPARTMENT, A.HOSPITAL, A.LEVEL FROM H_DOC A LEFT JOIN H_DOC_DEPARTMENT B ON A.DEPARTMENT = B.DEP_CODE WHERE A.DELETE_FLAG = 0
AND A.level BETWEEN #{LEVEL_BEGIN} AND #{LEVEL_END}
AND A.DEPARTMENT = #{DEPARTMENT}

SELECT COUNT(1) FROM H_DOC A WHERE A.DELETE_FLAG = 0
AND A.level BETWEEN #{LEVEL_BEGIN} AND #{LEVEL_END}
AND A.DEPARTMENT = #{DEPARTMENT}

mapper.update:
UPDATE H_SUBSCRIBE
SET	DESCRIPTION = #{DESCRIPTION}
WHERE UUID = #{UUID}

UPDATE H_SUBSCRIBE
SET	DIAGNOSE_STATUS = 4,
WHERE UUID = #{SUBSCRIBEID}

UPDATE H_SUBSCRIBE
SET	DELETE_FLAG = 1
WHERE UUID = #{UUID}
